INSERT INTO wisata (id, judul, gambar, deskripsi, jam_buka, jam_tutup, no_telepon, harga_tiket, link_gmaps, created_at, kategori, fasilitas, galeri, longitude, latitude, kode_wilayah, alamat, event) VALUES (21, 'Kolam Renang Kalionggok', 'utama/1765742883493-kalionggok.jpg', 'Sebagai wisata rekreasi bernuansa edukatif, Wisata Kali Onggok merupakan destinasi yang cukup populer di kalangan masyarakat Banjarnegara, khususnya di Kecamatan Rakit dan sekitarnya, yang berlokasi di Desa Blabar Lor, Adipasir, Rakit, Banjarnegara, Jawa Tengah. Kali Onggok menghadirkan beragam fasilitas dalam satu kawasan, mulai dari kolam pemancingan yang dilengkapi pemandu wisata, di mana hasil pancing dapat langsung diolah di tempat atau dibawa pulang, hingga kolam utama dengan fasilitas snorkeling yang airnya jernih dan dihiasi replika gunung, jembatan, serta susunan batu di dasar kolam. Selain itu, tersedia kolam renang dengan kedalaman bertahap yang aman untuk balita, anak-anak, hingga remaja dan dipercantik dengan lukisan 3D, serta ruang meeting yang dapat dimanfaatkan untuk berbagai acara seperti reuni, pernikahan, dan seminar. Sebagai pelengkap, Coffee Joke’s hadir sebagai kafe dan resto yang menyajikan aneka makanan dan minuman berkualitas dengan harga terjangkau, berbagai olahan ikan, paket barbeque, serta live music harian yang membuat suasana berwisata di Kali Onggok semakin nyaman dan menyenangkan.', '08:00', '17:00', '6285227353340', 5000.00, 'https://maps.app.goo.gl/2tuAHdFqy7BY2Kkv8', '2025-12-14 20:08:06', '["Wisata Rekreasi","Wisata Edukasi"]', '["Toilet","Parkir"]', '[]', '109.5424019219349', '-7.423012814203003', '33.04.11.2005', 'Blabar Lor, Adipasir, Kec. Rakit, Kab. Banjarnegara, Jawa Tengah', null);
